# finds the greatest product of 'n' consecutive digits in the number 'num'
def find_greatest_product(num, n)
	num_arr = num.to_s.split('').map {|x| x.to_i}
	i = 0; max_product = 0
	while i <= (num_arr.length - n)
		product = num_arr[i, n].inject(:*)
		max_product = product if product > max_product
		i += 1		
	end
	return max_product
end

